import * as React from "react";
import { useEffect, useState } from "react";

const DJANGO_HOST = process.env.DJANGO_HOST || "http://localhost:8000";

type DjangoDog = {
  model: string
  pk: number;
  status? : string;
  fields: {
    name: string;
    age:  number;
    my_email: string;
  };
};

type Dog = {
  id: number;
  name: string;
  age: number;
}

const convertDjangoToNormal = (djangoDog: DjangoDog) => {
  return {
    id: djangoDog.pk,
    name: djangoDog.fields.name,
    age: djangdoDog.fields.age,
  }
}

type DogsProps = {
  header?: string
}

const Dogs = () => {
  const [header, setHeader] = useState("")
  const [dogs, setDogs] = useState<DjangoDog[]>([]);

  useEffect(() => {
    fetch(`${DJANGO_HOST}/dogs/`)
      .then((res) => res.json())
      .then((data) => {
        setDogs(data);
      });
  }, []);

  const handleClick = () => {
    setHeader("new")
  }

  return (
    <>
      <h2>{header}</h2>
      <button onClick={handleClick}>Change header</button>
      <ul>
        {dogs.map((dog) => (
          <li key={dog.pk}>{dog.fields.name}</li>
        ))}
      </ul>
    </>
  );
};

export default Dogs;
